import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import DropDown from './DropDown';

var apiKey = "AIzaSyAqxjnnG7Sfva9oHu4Btuosrehfl5l_Qwo";
var playlistID = "PLPFcHFmFMXEDatE2hyXFz0iC8TMeo7xmL";
var URL = `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&playlistId=${playlistID}&key=${apiKey}`;

function App() {
  const [videos, setVideos] = useState([]);
  const [showing, setShowing] = useState(false)

  useEffect(() => {
    axios.get('http://localhost:5000/getvideos').then((response) => {
      setVideos(response.data);
      console.log(response.data)
    })
  }, [])



  return (
    <div className='App'>
      {videos.map((val, key) => {
        return <div key={key} className='video-container'>
          <div onClick={() => {
            setShowing(!showing); 
          }}><img alt={val.title} src={val.thumb} className='thumb'/></div>
          <a href={val.link} className='link'><h2>{val.title}</h2></a>
          {showing && <div className='video-dropdown'>
            <h1>{val.title}</h1>  
          </div>}
        </div>
      })}
    </div>
  )
}  
export default App;
