import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import DropDown from './DropDown';

var apiKey = "AIzaSyAqxjnnG7Sfva9oHu4Btuosrehfl5l_Qwo";
var playlistID = "PLPFcHFmFMXEDatE2hyXFz0iC8TMeo7xmL";
var URL = `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&playlistId=${playlistID}&key=${apiKey}`;

function App() {
  const [videos, setVideos] = useState([]);
  const [showing, setShowing] = useState(false)

  useEffect(() => {
    axios.get(URL).then((response) => {
      setVideos(response.data.items);
    })
  }, [])

  const handleClick = (e) => {
    setShowing(!showing);
  }

  //get youtube thumbnail for each video
  const getThumbnail = (video) => {
    return video.snippet.thumbnails.medium.url;
  }

  console.log(videos);

  return (
    <div className='App'>
      <img src="https://i.imgur.com/QQZQZ.png" alt="Jerma Logo" className="logo" />
    </div>
  )
}  
export default App;
