import React from 'react';

const VideoModal = (props) => {
    let playingVideo = props.playingVideo;
    let setShowing = props.setShowing;
    console.log(playingVideo);
    let videoID = playingVideo.snippet.resourceId.videoId;
    return <div className='modal-background'>
        <div className='modal-container'>
            <div className='modal-close-container'>
                <button className='modal-close-button' onClick={() => setShowing(false)}>X</button>
            </div>
            <div className='modal-video-container'>
                <iframe className='modal-video' 
                src={`https://www.youtube.com/embed/${videoID}`} 
                frameBorder='0' 
                allow='accelerometer; 
                autoplay; 
                encrypted-media; 
                gyroscope; 
                picture-in-picture' 
                allowFullScreen></iframe>
            </div>
            <div className='modal-video-info-container'>
                <h2 className='modal-video-title'>{playingVideo.snippet.title}</h2>
                <p className='modal-video-description'>{playingVideo.snippet.description}</p>
            </div>
        </div>
    </div>
}

export default VideoModal;
